    /**
     * 别人的答案
     * @param s
     * @param words
     * @return
     */
    public List<Integer> findSubstring(String s, String[] words) {
        if(words==null||words.length==0){
            return new ArrayList();
        }
        Map<String,Integer> tmp,map=new HashMap<String,Integer>();
        int lens=s.length(),lenw=words[0].length();
        for(int i=0;i<words.length;i++){
            if(map.containsKey(words[i])){
                map.put(words[i],map.get(words[i])+1);
            }else{
                map.put(words[i],1);
            }
        }
        List<Integer> res = new ArrayList<Integer>();
        for(int i=0;i<=lens-lenw*words.length;i++){
            tmp=new HashMap<String,Integer>();
            int j=0;
            for(;j<words.length;j++){
                int pos=i+j*lenw;
                String sub=s.substring(pos,pos+lenw);
                if(map.containsKey(sub)){
                    int num=0;
                    if(tmp.containsKey(sub))num=tmp.get(sub);
                    if(map.get(sub) < num+1)break;
                    else tmp.put(sub,num+1);
                }else break;
            }
            if(j>=words.length){
                res.add(i);
            }
        }
        return res;
    }

    /**
     * 我的答案，超时了，但我觉得是对的，不过也没测出来正确还是错
     * @param s
     * @param words
     * @return
     */
    public List<Integer> findSubstringWODE(String s, String[] words) {
        List<Integer> result = new ArrayList();
        for(int i=0;i<s.length();i++){
            String temp = s.substring(i);
            for(int j=0;j<words.length;j++){
                int l = words[j].length();
                if(i+l<=s.length()){
                    if(temp.indexOf(words[j])==0){
                        if(my(temp.substring(l),subArray(words,j))){
                            result.add(i);
                            break;
                        }
                    }
                }
            }
        }
        return result;
    }

    //todo 是否符合匹配
    public boolean my(String s,String[] words){
        if(words==null||words.length==0){
            return true;
        }
        if(s.length()==0){
            return false;
        }
        for(int i=0;i<words.length;i++){
            if(s.indexOf(words[i])==0){
                return my(s.substring(words[i].length()),subArray(words,i));
            }
        }
        return false;
    }
    //移除数组里的某一项
    public String[] subArray(String[] words,int j){
        if(words.length<=1){
            return null;
        }else{
            String[] temp = new String[words.length-1];
            for(int i=0,k=0;i<words.length;i++){
                if(i==j){
                    continue;
                }else{
                    temp[k++]=words[i];
                }
            }
            return temp;
        }
    }
